package co.edu.poo2.ProtectoCazaUnac;import java.util.Vector;import static co.edu.poo2.ProtectoCazaUnac.ListaUsuario.mostrar;public class Usuario {    private String user;    private String password;    private String password2;    public String getUser() {        return user;    }    public void setUser(String user) {        this.user = user;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getPassword2() {        return password2;    }    public void setPassword2(String password2) {        this.password2 = password2;    }    public static int verificarUsuario(String usuario){        Vector lista = mostrar();        Usuario obj;        for (int i = 0; i<lista.size(); i++){            obj = (Usuario) lista.elementAt(i);            if (obj.getUser().equalsIgnoreCase(usuario)){                return i;            }        }        return -1;    }    public static int verificarLogin(String usuario,String password){        Vector lista = mostrar();        Usuario obj;        for(int i = 0;i<lista.size();i++){            obj = (Usuario) lista.elementAt(i);            if(obj.getUser().equalsIgnoreCase(usuario)&& obj.getPassword2().equalsIgnoreCase(password)){                return i;            }        }        return -1;    }    public static Vector mostrar(){        return ListaUsuario.mostrar();    }}